import React from 'react';
import { gql, useQuery } from '@apollo/client';
import { Loading } from '../components';

// import { RouteComponentProps } from '@reach/router';

export const GET_BIKE_BY_ID = gql`
  query GetBikeById ($id: ID!) {
    bike (bike_id: $id){
      bike_id
      lat
      lon
      is_reserved
      is_disabled
      vehicle_type
    }
  }
`;

const Bike = ({id}) => {
  const { data, loading, error } = useQuery(GET_BIKE_BY_ID,{ variables: { id } });

  if (loading) return <Loading />;
  if (error) return <p>ERROR</p>;
  if (!data.bike) return <p>Not found</p>;
  console.log(data);
  return <div>
    <p> {data.bike.bike_id }</p>
    <p> {data.bike.lat }</p>
    <p> {data.bike.lon }</p>
    <p> {data.bike.is_reserved?'yes':'no' }</p>
    <p> {data.bike.is_disabled?'yes':'no' }</p>
    <p> {data.bike.vehicle_type }</p>
    </div>;

}

export default Bike;
