// eslint-disable-next-line
import React, {setState, useState, useEffect} from 'react';
import { gql, useQuery } from '@apollo/client';
import { Loading } from '../components';
import { Link } from '@reach/router';

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import TextField from'@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

export const GET_BIKES = gql`
  query GetBikeList {
    bikes{
      bike_id
      lat
      lon
      is_reserved
      is_disabled
      vehicle_type
    }
  }
`;
export const GET_BIKE_BY_ID = gql`
  query GetBikeById ($id: ID!) {
    bike (bike_id: $id){
      bike_id
      lat
      lon
      is_reserved
      is_disabled
      vehicle_type
    }
  }
`;

const Bikes = () => {
  const { data, loading, error } = useQuery(GET_BIKES);

  const [input, setInput] = useState('');
  const [bikeSearched, setBikeSearched] = useState();

  const classes = makeStyles();

  if (loading) return <Loading />;
  if (error) return <p>ERROR</p>;
  if (!data) return <p>Not bike list found</p>;

  return (
    <div>
      <form className={classes.root} noValidate autoComplete="off">
        <TextField 
          id="standard-basic" 
          label="Search by Bike id" 
          value = {input}
          onChange = { e => setInput(e.target.value) }
        />
        <label htmlFor="contained-button-file">
          <Link to={`/${input}`}>
            <Button variant="contained" color="primary">
              Search
            </Button>
          </Link>
        </label>
      </form >

      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Bike ID</TableCell>
              <TableCell align="right">Latitude</TableCell>
              <TableCell align="right">Longitude</TableCell>
              <TableCell align="right">Reserved</TableCell>
              <TableCell align="right">Disabled</TableCell>
              <TableCell align="right">Type</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.bikes.map((bike) => (
              <TableRow key={bike.bike_id}>
                <TableCell component="th" scope="row">{bike.bike_id}</TableCell>
                <TableCell align="right">{bike.lat}</TableCell>
                <TableCell align="right">{bike.lon}</TableCell>
                <TableCell align="right">{bike.is_reserved? 'yes':'no'}</TableCell>
                <TableCell align="right">{bike.is_disabled? 'yes':'no'}</TableCell>
                <TableCell align="right">{bike.vehicle_type}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default Bikes;
